import React from 'react';
import './index.css';
import Clock from './Clock';


let currentTime = setInterval(() => {
    let a;
    let time;
    let date;
    let getHour;
    let getMinute;
    let getSecond;
    let am_pm;
    a = new Date();
    date = a.toDateString()
    getHour = a.getHours() > 12 ? a.getHours() - 12 : a.getHours();
    getHour = getHour < 10 ? "0" + getHour : getHour;
    getMinute = a.getMinutes() < 10 ? "0" + a.getMinutes() : a.getMinutes();
    getSecond = a.getSeconds() < 10 ? "0" + a.getSeconds() : a.getSeconds();
    am_pm = getHour >= 12 ? " PM" : " AM";
    time = getHour + ":" + getMinute + ":" + getSecond + am_pm;

    document.getElementsByClassName("time").innerHTML = time + "<br>" + date;

}, 1000)



let time = new Date().getHours();
let greet;

let greetStyle = {
    color: ''
}

if (time >= 1 && time < 12) {
    greet = 'Good Morning';
    greetStyle.color = '#1eba62';
}
else if (time >= 12 && time <= 7) {
    greet = 'Good Afternoon';
    greetStyle.color = '#ed943b';
}
else {
    greet = 'Good Night';
    greetStyle.color = '#333232';
}

function Greet() {
    return(
        <div className='greet_div'>
            <h1>Hello Sir, <span style={greetStyle}> {greet} </span></h1>
            <p>Current Time is: <span className='time'> {currentTime} </span> </p>
        </div>
    );
}

export default Greet;
