import React from 'react';

let time = new Date().getHours();
let greet;

let greetStyle = {
    color: ''
}

if(time >=1 && time <12){
    greet = 'Good Morning';
    greetStyle.color = '#1eba62';
} 
else if(time >= 12 && time <= 7){
    greet = 'Good Afternoon';
    greetStyle.color ='#ed943b';
}
else{
    greet = 'Good Night';
    greetStyle.color = '#333232';
}

function GreatTime() {
    return(
        greet, greetStyle
    );
}

export default GreatTime;
