import React from 'react';
import './index.css';
import Clock from './Clock';


let time = new Date().getHours();
let greet;

let greetStyle = {
    color: ''
}

if (time >= 1 && time < 12) {
    greet = 'Good Morning';
    greetStyle.color = '#1eba62';
}
else if (time >= 12 && time <= 7) {
    greet = 'Good Afternoon';
    greetStyle.color = '#ed943b';
}
else {
    greet = 'Good Night';
    greetStyle.color = '#333232';
}

function Greet() {
    return(
        <div className='greet_div'>
            <h1>Hello Sir, <span style={greetStyle}> {greet} </span></h1>
            <p>Current Time is: {Clo}</p>
        </div>
    );
}

export default Greet;
